    private void balokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_balokActionPerformed
        int panjang,lebar, tinggi;
        panjang=Integer.parseInt(pj.getText());
        lebar=Integer.parseInt(lb.getText());
        tinggi=Integer.parseInt(tg.getText());
        int hasil=panjang*lebar*tinggi;
        try {
            JOptionPane.showMessageDialog(null,"Volume Balok Adalah :"+hasil,"luas",
                    JOptionPane.INFORMATION_MESSAGE);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,"Error:"+e,"Gagal",
                    JOptionPane.WARNING_MESSAGE);
        }
      
        // TODO add your handling code here:
    }//GEN-LAST:event_balokActionPerformed

